import React, { useState } from "react";
import axios from 'axios';
import { Button, ControlLabel, FormGroup, FormControl, PageHeader } from "react-bootstrap";
import "./profileEdit.css";

export default function ProfileEdit(props) {
  const [firstName, setFirstName] = useState("FirstName");
  const [lastName, setLastName] = useState("LastName");
  const [college, setCollege] = useState("College");
  const [major, setMajor] = useState("Major");
  const [biography, setBiography] = useState("Your biography goes here.");
  const [skills, setSkills] = useState("Your skills go here.");
  const [picture, setPicture] = useState("/profile.png");
  const [resume, setResume] = useState("Your resume goes here.");

  function editProfile() {
    alert(props.userID);
  }

  function handleSubmit(event) {
    event.preventDefault();

    props.history.push("/profile");
  }

  return (
    <div className="ProfileEdit">
      <form onSubmit={handleSubmit}>
        <PageHeader>
          Edit Profile
          <Button className="pull-right" bsSize="small" type="submit">
            Save
          </Button>
        </PageHeader>
        <FormGroup bsSize="large">
          <ControlLabel>First Name</ControlLabel>
          <FormControl
            value={firstName}
            onChange={e => setFirstName(e.target.value)}
          />
        </FormGroup>
        <FormGroup bsSize="large">
          <ControlLabel>Last Name</ControlLabel>
          <FormControl
            value={lastName}
            onChange={e => setLastName(e.target.value)}
          />
        </FormGroup>
        <FormGroup bsSize="large">
          <ControlLabel>College</ControlLabel>
          <FormControl componentClass="select">
            value={firstName}
            onChange={e => setFirstName(e.target.value)}
            </FormControl>
        </FormGroup>
      </form>
    </div>
  );
}
