import React, { useState, useEffect } from "react";
import axios from 'axios';
import { Button, ControlLabel, FormGroup, FormControl, PageHeader } from "react-bootstrap";
import "./profileEdit.css";

export default function ProfileEdit(props) {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [biography, setBiography] = useState("");
  const [college, setCollege] = useState(null);
  const [major, setMajor] = useState(null);
  const [skills, setSkills] = useState(null);
  const [picture, setPicture] = useState(null);
  const [resume, setResume] = useState(null);

  const [isLoading, hasLoaded] = useState(true);

  useEffect(() => {
    if (isLoading) {
      axios.get('http://localhost:3000/user/' + props.userID)
        .then(res => {
          setFirstName(res.data.firstName);
          setLastName(res.data.lastName);
          setBiography(res.data.biography);
          setCollege(res.data.college);
          //setMajor(res.data.major);
          setSkills(res.data.skills);
          setPicture(res.data.picture);
          setResume(res.data.resume);
        });
      hasLoaded(false);
    }
  });

  function handleSubmit(event) {
    event.preventDefault();

    const user = {
      firstName: firstName,
      lastName: lastName,
      biography: biography
    }

    axios.post('http://localhost:3000/user/update' + props.userID, user)
      .then(() => alert("updated"))
      .catch(() => alert("error"));

    props.history.push("/profile");
  }

  return (
    <div className="ProfileEdit">
      <form onSubmit={handleSubmit}>
        <PageHeader>
          Edit Profile
          <Button className="pull-right" bsSize="small" type="submit">
            Save
          </Button>
        </PageHeader>
        <FormGroup bsSize="large">
          <ControlLabel>First Name</ControlLabel>
          <FormControl
            value={firstName}
            onChange={e => setFirstName(e.target.value)}
          />
        </FormGroup>
        <FormGroup bsSize="large">
          <ControlLabel>Last Name</ControlLabel>
          <FormControl
            value={lastName}
            onChange={e => setLastName(e.target.value)}
          />
        </FormGroup>
        <FormGroup bsSize="large">
          <ControlLabel>College</ControlLabel>
          <FormControl 
            componentClass="select"
            value={college}
            onChange={e => setCollege(e.target.value)}
          >
            <option value="select">...</option>
            <option value="option1">Option1</option>
            <option value="option2">Option2</option>
          </FormControl>
        </FormGroup>
        <FormGroup bsSize="large">
          <ControlLabel>Major</ControlLabel>
          <FormControl 
            componentClass="select"
            value={major}
            onChange={e => setMajor(e.target.value)}
          >
            <option value="select">...</option>
            <option value="option1">Option1</option>
            <option value="option2">Option2</option>
          </FormControl>
        </FormGroup>
        <FormGroup bsSize="large">
          <ControlLabel>Biography</ControlLabel>
          <FormControl
            componentClass="textarea"
            value={biography}
            onChange={e => setBiography(e.target.value)}
          />
        </FormGroup>
      </form>
    </div>
  );
}
