import React, { useState, useEffect } from "react";
import axios from 'axios';
import { Button, Media, PageHeader, Tab, Tabs } from "react-bootstrap";
import "./profile.css";

export default function Profile(props) {
  const [firstName, setFirstName] = useState("FirstName");
  const [lastName, setLastName] = useState("LastName");
  const [biography, setBiography] = useState("Your biography goes here.");
  const [college, setCollege] = useState("College");
  const [major, setMajor] = useState("Major");
  const [skills, setSkills] = useState("Your skills go here.");
  const [picture, setPicture] = useState("/profile.png");
  const [resume, setResume] = useState("Your resume goes here.");

  useEffect(() => {
    axios.get('http://localhost:3000/user/' + props.userID)
      .then(res => {
        setFirstName(res.data.firstName);
        setLastName(res.data.lastName);
        setBiography(res.data.biography);
      })
      .catch(error => console.log(error));
  });

  function editProfile() {
    props.history.push("/profile/edit")
  }

  return (
    <div className="Profile">
      <PageHeader>
        Profile
        <Button className="pull-right" bsSize="medium" type="submit" onClick={editProfile} >
          Edit
        </Button>
      </PageHeader>
      <Media>
        <Media.Left>
          <img width={150} height={150} src={picture} />
        </Media.Left>
        <Media.Body>
          <Media.Heading>{firstName} {lastName}</Media.Heading>
          <h4>{college}</h4>
          <h4>{major}</h4>
        </Media.Body>
      </Media>
      <Tabs animation={false}>
        <Tab eventKey={1} title="About">
          <p>{biography}</p>
        </Tab>
        <Tab eventKey={2} title="Skills">
          <p>{skills}</p>
        </Tab>
        <Tab eventKey={3} title="Resume">
          <p>{resume}</p>
        </Tab>
      </Tabs>
    </div>
  );
}
